package com.xuyuan.chart.fusionchart.dao;import java.util.List;import org.springframework.jdbc.core.JdbcTemplate;//@Repository("browserDao")//@Transactional(propagation=Propagation.SUPPORTS, readOnly=true)public class BrowserDao {	//@Resource(name="jdbcTemplate")	private JdbcTemplate jdbcTemplate;		private static final String SQL_GET_BROWER_STAT = 		"select statVar,statCount from browser where statType='browser'";	//获取记录的浏览器信息		public List getBrowserStat() {		return jdbcTemplate.queryForList(SQL_GET_BROWER_STAT);	}	public void setJdbcTemplate(JdbcTemplate jdbcTemplate) {		this.jdbcTemplate = jdbcTemplate;	}	}