package com.xuyuan.chart.jfreechart.helloworld;import java.io.FileOutputStream;import org.jfree.chart.ChartFactory;import org.jfree.chart.ChartUtilities;import org.jfree.chart.JFreeChart;import org.jfree.chart.plot.PlotOrientation;import org.jfree.data.xy.XYSeries;import org.jfree.data.xy.XYSeriesCollection;public class XYLineChart {	public static void main(String[] args) throws Exception {		XYSeriesCollection dataset = new XYSeriesCollection();		XYSeries series1 = new XYSeries("XYSeris");		series1.add(1, 2);		series1.add(2, 4);		series1.add(7, 6);		dataset.addSeries(series1);		JFreeChart chart = ChartFactory.createXYLineChart("XYAxis", "X", "Y", dataset, PlotOrientation.VERTICAL, true, true, false);		FileOutputStream fos = new FileOutputStream("/tmp/xy-axis.png");		ChartUtilities.writeChartAsPNG(fos, chart, 400, 300);	}}