package com.xuyuan.commons.poi.annotation.test;import java.io.File;import java.util.ArrayList;import java.util.List;import com.xuyuan.commons.poi.annotation.ImportExcel;import com.xuyuan.commons.poi.annotation.bean.Testpojo;public class ImportExcelTest {	public static void main(String[] args) {		ImportExcel<Testpojo> test = new ImportExcel(Testpojo.class);		File file = new File("D:\\testOne.xls");		Long befor = System.currentTimeMillis();		List<Testpojo> result = (ArrayList) test.importExcel(file);		Long after = System.currentTimeMillis();		System.out.println("此次操作共耗时：" + (after - befor) + "毫秒");		for (int i = 0; i < result.size(); i++) {			 Testpojo testpojo=result.get(i);			 System.out.println("导入的信息为："+testpojo.getLoginname()+ "----"+testpojo.getAge()+"---"+testpojo.getMoney()+"-----"+testpojo.getCreatetime());		}		System.out.println("共转化为List的行数为：" + result.size());	}}