package com.xuyuan.extjs3.tree.bean;import java.util.List;public class Menu {	private Integer id;	private String text;	private String link;	private String cls;	private boolean leaf;	private List<Menu> children;		public Menu() {	}	public Menu(Integer id, String text, String cls, boolean leaf) {		this.id = id;		this.text = text;		this.cls = cls;		this.leaf = leaf;	}		public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public String getText() {		return text;	}	public void setText(String text) {		this.text = text;	}	public boolean isLeaf() {		return leaf;	}	public void setLeaf(boolean leaf) {		this.leaf = leaf;	}	public String getLink() {		return link;	}	public void setLink(String link) {		this.link = link;	}	public List<Menu> getChildren() {		return children;	}	public void setChildren(List<Menu> children) {		this.children = children;	}	public String getCls() {		return cls;	}	public void setCls(String cls) {		this.cls = cls;	}	}