package com.xuyuan.hibernate3.model;import java.util.Set;public class Department {	/**	 * @uml.property  name="id"	 */	private int id ;	/**	 * @uml.property  name="name"	 */	private String name;	/**	 * @uml.property  name="emps"	 * @uml.associationEnd  multiplicity="(0 -1)" inverse="depart:com.xuyuan.hibernate3.model.Employee"	 */	private Set<Employee> emps;	public Set<Employee> getEmps() {		return emps;	}	public void setEmps(Set<Employee> emps) {		this.emps = emps;	}	/**	 * @return	 * @uml.property  name="id"	 */	public int getId() {		return id;	}	/**	 * @param id	 * @uml.property  name="id"	 */	public void setId(int id) {		this.id = id;	}	/**	 * @return	 * @uml.property  name="name"	 */	public String getName() {		return name;	}	/**	 * @param name	 * @uml.property  name="name"	 */	public void setName(String name) {		this.name = name;	}}