package com.xuyuan.hibernate3.model;public class Employee {	/**	 * @uml.property  name="id"	 */	private int id;	/**	 * @uml.property  name="name"	 */	private String name;	/**	 * @uml.property  name="depart"	 * @uml.associationEnd  inverse="emps:com.xuyuan.hibernate3.model.Department"	 */	private Department depart;	/**	 * @return	 * @uml.property  name="id"	 */	public int getId() {		return id;	}	/**	 * @param id	 * @uml.property  name="id"	 */	public void setId(int id) {		this.id = id;	}	/**	 * @return	 * @uml.property  name="name"	 */	public String getName() {		return name;	}	/**	 * @param name	 * @uml.property  name="name"	 */	public void setName(String name) {		this.name = name;	}	/**	 * @return	 * @uml.property  name="depart"	 */	public Department getDepart() {		return depart;	}	/**	 * @param depart	 * @uml.property  name="depart"	 */	public void setDepart(Department depart) {		this.depart = depart;	}}