package com.xuyuan.hibernate3.model;import java.util.Date;public class IdCard {	/**	 * @uml.property  name="id"	 */	private int id;	/**	 * @uml.property  name="validity"	 */	private Date validity;	/**	 * @uml.property  name="person"	 * @uml.associationEnd  inverse="idCard:com.xuyuan.hibernate3.model.Person"	 */	private Person person;	/**	 * @return	 * @uml.property  name="id"	 */	public int getId() {		return id;	}	/**	 * @param id	 * @uml.property  name="id"	 */	public void setId(int id) {		this.id = id;	}	/**	 * @return	 * @uml.property  name="validity"	 */	public Date getValidity() {		return validity;	}	/**	 * @param validity	 * @uml.property  name="validity"	 */	public void setValidity(Date validity) {		this.validity = validity;	}	/**	 * @return	 * @uml.property  name="person"	 */	public Person getPerson() {		return person;	}	/**	 * @param person	 * @uml.property  name="person"	 */	public void setPerson(Person person) {		this.person = person;	}}