package com.xuyuan.hibernate3.model;public class Person {	/**	 * @uml.property  name="id"	 */	private int id;	/**	 * @uml.property  name="name"	 */	private String name;	/**	 * @uml.property  name="idCard"	 * @uml.associationEnd  inverse="person:com.xuyuan.hibernate3.model.IdCard"	 */	private IdCard idCard;	/**	 * @return	 * @uml.property  name="id"	 */	public int getId() {		return id;	}	/**	 * @param id	 * @uml.property  name="id"	 */	public void setId(int id) {		this.id = id;	}	/**	 * @return	 * @uml.property  name="name"	 */	public String getName() {		return name;	}	/**	 * @param name	 * @uml.property  name="name"	 */	public void setName(String name) {		this.name = name;	}	/**	 * @return	 * @uml.property  name="idCard"	 */	public IdCard getIdCard() {		return idCard;	}	/**	 * @param idCard	 * @uml.property  name="idCard"	 */	public void setIdCard(IdCard idCard) {		this.idCard = idCard;	}}