package com.xuyuan.hibernate3.model;public class User {	private Integer id;	private String username;	private String password;	private String email;	private String phone;	private String dept;		public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public String getUsername() {		return username;	}	public void setUsername(String username) {		this.username = username;	}	public String getPassword() {		return password;	}	public void setPassword(String password) {		this.password = password;	}	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}	public String getPhone() {		return phone;	}	public void setPhone(String phone) {		this.phone = phone;	}	public String getDept() {		return dept;	}	public void setDept(String dept) {		this.dept = dept;	}		@Override	public String toString() {		return "id:" + this.id + "; username:" + this.username + ";email:" + this.email;	}}