package com.xuyuan.hibernate3.util;import org.hibernate.Session;import org.hibernate.SessionFactory;import org.hibernate.cfg.Configuration;public class SessionFactoryManager {	private static Configuration configuration = null;	private static SessionFactory sessionFactory = null;	private SessionFactoryManager() {	}	static {		configuration = new Configuration();		configuration.configure();		sessionFactory = configuration.buildSessionFactory();	}	public static SessionFactory openSessionFactory() {		return sessionFactory;	}	public static Session getSession() {		return sessionFactory.openSession();	}	public static void closeSessionFactory() {		if (sessionFactory != null && sessionFactory.isClosed() == false) {			sessionFactory.close();		}	}}