package com.xuyuan.ibatis.model;public class User {	/**	 * @uml.property  name="id"	 */	private Long id;	/**	 * @uml.property  name="username"	 */	private String username;	/**	 * @uml.property  name="password"	 */	private String password;	/**	 * @uml.property  name="email"	 */	private String email;		/**	 * @return	 * @uml.property  name="id"	 */	public Long getId() {		return id;	}	/**	 * @param id	 * @uml.property  name="id"	 */	public void setId(Long id) {		this.id = id;	}	/**	 * @return	 * @uml.property  name="username"	 */	public String getUsername() {		return username;	}	/**	 * @param username	 * @uml.property  name="username"	 */	public void setUsername(String username) {		this.username = username;	}	/**	 * @return	 * @uml.property  name="password"	 */	public String getPassword() {		return password;	}	/**	 * @param password	 * @uml.property  name="password"	 */	public void setPassword(String password) {		this.password = password;	}	/**	 * @return	 * @uml.property  name="email"	 */	public String getEmail() {		return email;	}	/**	 * @param email	 * @uml.property  name="email"	 */	public void setEmail(String email) {		this.email = email;	}	@Override	public String toString() {		return "id:" + this.id + " name:" + this.username + " mail:" + this.email;	}	}