package com.xuyuan.nosql.mongodb.morphia.entity;import java.util.Date;import org.bson.types.ObjectId;import org.codehaus.jackson.annotate.JsonIgnore;import org.codehaus.jackson.map.annotate.JsonDeserialize;import org.codehaus.jackson.map.annotate.JsonSerialize;import com.github.jmkgreen.morphia.annotations.Entity;import com.github.jmkgreen.morphia.annotations.Id;import com.github.jmkgreen.morphia.annotations.PostPersist;import com.github.jmkgreen.morphia.annotations.PrePersist;/** * Java对象到MongoDB文档和Java对象到输出JSON对象的POJO实体 * @author Administrator * *///morphia中的注解 标明该对象存入orderInfo集合中 并且不存储类名@Entity(value="orderInfo", noClassnameStored= true)public class OrderInfo {	//morphia中的注解 标明该key为标识字段(MongoDB中特殊的ObjectId字段)    /**	 * @uml.property  name="id"	 * @uml.associationEnd  	 */    @Id    //Jackson中的注解 标明在序列化与反序列化过程中不使用该key    @JsonIgnore(value= true)    private ObjectId id;    /**	 * @uml.property  name="orderId"	 */    private Long orderId;    /**	 * @uml.property  name="productName"	 */    private String productName;    /**	 * @uml.property  name="quantity"	 */    private Integer quantity;    /**	 * @uml.property  name="unitPrice"	 */    private Float unitPrice;    //Jackson中的注解 标明该字段使用自定义的DateSerializer类实现序列化    /**	 * @uml.property  name="orderDate"	 */    @JsonSerialize(using= DateSerializer.class)    //Jackson中的注解 标明该字段使用自定义的DateDeserializer类实现反序列化    @JsonDeserialize(using= DateDeserializer.class)    private Date orderDate;    /**	 * @uml.property  name="contactName"	 */    private String contactName;    /**	 * @uml.property  name="address"	 */    private String address;    /**	 * @uml.property  name="phone"	 */    private String phone;    @SuppressWarnings("unused")    //morphia中的注解 指示在存入MongoDB之前的操作    @PrePersist    private void beforeSaving(){        System.out.println("即将保存对象:"+ this.toString());    }    @SuppressWarnings("unused")    //morphia中的注解 指示在存入MongoDB之后的操作    @PostPersist    private void afterSaving(){        System.out.println("对象保存完毕:"+ this.toString());    }    //以下就是Getters和Setters了    /**	 * @return	 * @uml.property  name="id"	 */    public ObjectId getId() {        return id;    }    /**	 * @param id	 * @uml.property  name="id"	 */    public void setId(ObjectId id) {        this.id = id;    }    /**	 * @return	 * @uml.property  name="orderId"	 */    public Long getOrderId() {        return orderId;    }    /**	 * @param orderId	 * @uml.property  name="orderId"	 */    public void setOrderId(Long orderId) {        this.orderId = orderId;    }    /**	 * @return	 * @uml.property  name="productName"	 */    public String getProductName() {        return productName;    }    /**	 * @param productName	 * @uml.property  name="productName"	 */    public void setProductName(String productName) {        this.productName = productName;    }    /**	 * @return	 * @uml.property  name="quantity"	 */    public Integer getQuantity() {        return quantity;    }    /**	 * @param quantity	 * @uml.property  name="quantity"	 */    public void setQuantity(Integer quantity) {        this.quantity = quantity;    }    /**	 * @return	 * @uml.property  name="unitPrice"	 */    public Float getUnitPrice() {        return unitPrice;    }    /**	 * @param unitPrice	 * @uml.property  name="unitPrice"	 */    public void setUnitPrice(Float unitPrice) {        this.unitPrice = unitPrice;    }    /**	 * @return	 * @uml.property  name="orderDate"	 */    public Date getOrderDate() {        return orderDate;    }    /**	 * @param orderDate	 * @uml.property  name="orderDate"	 */    public void setOrderDate(Date orderDate) {        this.orderDate = orderDate;    }    /**	 * @return	 * @uml.property  name="contactName"	 */    public String getContactName() {        return contactName;    }    /**	 * @param contactName	 * @uml.property  name="contactName"	 */    public void setContactName(String contactName) {        this.contactName = contactName;    }    /**	 * @return	 * @uml.property  name="address"	 */    public String getAddress() {        return address;    }    /**	 * @param address	 * @uml.property  name="address"	 */    public void setAddress(String address) {        this.address = address;    }    /**	 * @return	 * @uml.property  name="phone"	 */    public String getPhone() {        return phone;    }    /**	 * @param phone	 * @uml.property  name="phone"	 */    public void setPhone(String phone) {        this.phone = phone;    }}