package com.xuyuan.nosql.mongodb.morphia.util;import java.net.UnknownHostException;import com.github.jmkgreen.morphia.Datastore;import com.github.jmkgreen.morphia.Morphia;import com.mongodb.Mongo;import com.mongodb.MongoException;/** * MongoDB联合Morphia框架生成DataStore的类 * 功能: 实现一个Morphia框架与MongoDB的集成方法 * @author chaijunkun * */public class MongoDBDataStore {    /**     * 生成Orders数据库Datastore对象     * @return 返回Datastore对象 发生异常返回为null     */    public static Datastore getOrdersInstance(){        Mongo connection = null;        try {            connection = new Mongo(MongoDBConnConfig.SERVER, MongoDBConnConfig.PORT);        } catch (UnknownHostException e) {            return null;        } catch (MongoException e) {            return null;        }        Morphia morphia= new Morphia();        return morphia.createDatastore(connection, MongoDBConnConfig.ORDERS_DATABASE);    }}