package com.xuyuan.pager.action;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import com.opensymphony.xwork2.ActionSupport;import com.xuyuan.pager.model.PageBean;import com.xuyuan.pager.service.IBooksService;/** * Hibernate+Spring+Struts2整合开发中的一个分页显示方案 * 仅演示不带查询条件的分页查询:	http://localhost:8888/start_j2ee/pageBooks.action * @author http://www.blogjava.net/rongxh7/archive/2008/11/29/243456.html * *///@Controller @Scope("prototype")public class PageAction  extends ActionSupport{		//@Autowired	private IBooksService booksService;		private int page;    			//第几页.页面传递过来的参数    private PageBean pageBean;    	//包含分页信息的bean.实体类Books都已经包含在PageBean里.因为在Service层pageBean.setList(list);        @Override    public String execute() throws Exception {        //分页的pageBean,参数pageSize表示每页显示记录数,page为当前页.     	//pageSize也可以在页面上自定义传递进来.比如让用户进行选择每页显示的数目.        pageBean = booksService.queryForPage(10, page);        return SUCCESS;    }    	public int getPage() {		return page;	}	public void setPage(int page) {		this.page = page;	}	public PageBean getPageBean() {		return pageBean;	}	public void setPageBean(PageBean pageBean) {		this.pageBean = pageBean;	}	public IBooksService getBooksService() {		return booksService;	}	public void setBooksService(IBooksService booksService) {		this.booksService = booksService;	}    }