package com.xuyuan.pager_ftl.action.ibatis;import com.xuyuan.pager_ftl.action.BaseAction;import com.xuyuan.pager_ftl.dao.ibatis.PagingList;import com.xuyuan.pager_ftl.service.ibatis.BookPageServiceIbatis;public class BookPageActionIbatis extends BaseAction {		private BookPageServiceIbatis bookService;	public PagingList userList;		public String list() {		userList = bookService.getAllUsers();		return "list";	}		public void setBookService(BookPageServiceIbatis bookService) {		this.bookService = bookService;	}	public PagingList getUserList() {		return userList;	}}