package com.xuyuan.pager_ftl.service.ibatis;import org.springframework.orm.ibatis.SqlMapClientTemplate;import com.xuyuan.ibatis.util.ParameterMap;import com.xuyuan.pager_ftl.dao.ibatis.PagingList;public class BookPageServiceIbatis extends BaseServiceIbatis{	private SqlMapClientTemplate sqlMapClientTemplate;	public void setSqlMapClientTemplate(SqlMapClientTemplate sqlMapClientTemplate) {		this.sqlMapClientTemplate = sqlMapClientTemplate;	}	public PagingList getAllUsers() {		ParameterMap map = new ParameterMap("NUM", 1);		return getPagingList("selectAllUsers", map, sqlMapClientTemplate);	}}