package com.xuyuan.pager_ftl.util;import java.io.UnsupportedEncodingException;import java.net.URLEncoder;import java.util.Iterator;import java.util.Map;import com.xuyuan.util.Constants;public class QueryUtil {		/**	 * 将请求参数还原为key=value的形式	 * @param params	 */	public static String getQueryString(Map<String, Object> params) {		StringBuffer queryString = new StringBuffer(256);		Iterator it = params.keySet().iterator();		int count = 0;		while (it.hasNext()) {			String key = (String) it.next();			String[] param = (String[]) params.get(key);			for (int i = 0; i < param.length; i++) {				if (count == 0) {					count++;				} else {					queryString.append("&");				}				queryString.append(key);				queryString.append("=");				try {					queryString.append(URLEncoder.encode((String) param[i], Constants.ENCODING));				} catch (UnsupportedEncodingException e) {				}			}		}		return queryString.toString();	}}