package com.xuyuan.quartz.example;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.quartz.Job;import org.quartz.JobExecutionContext;import org.quartz.JobExecutionException;public class MyJobClass implements Job{	static Log logger = LogFactory.getLog(MyJobClass.class);	public void execute(JobExecutionContext paramJobExecutionContext)			throws JobExecutionException {		System.out.println("It's Quartz HelloWorld....");		logger.info("It's Quartz HelloWorld....");	}}