package com.xuyuan.quartz.memory;import java.util.Date;import org.quartz.JobExecutionContext;import org.quartz.JobExecutionException;import org.springframework.scheduling.quartz.QuartzJobBean;/** * 可放入Dao层的类作为成员变量,和数据库进行交互.多个Dao类,配置在jobDataAsMap的map标签里 * @author Administrator * */public class SimpleJobDetail extends QuartzJobBean{	/**	 * @uml.property  name="message"	 */	private String message;	/**	 * @return	 * @uml.property  name="message"	 */	public String getMessage() {		return message;	}	/**	 * @param message	 * @uml.property  name="message"	 */	public void setMessage(String message) {		this.message = message;	}	@Override	protected void executeInternal(JobExecutionContext context) throws JobExecutionException {		System.err.println(new Date() + "SimpleJobDetail task run.............msg>>>>" + message);	}}