package com.xuyuan.quartz.spring;import java.util.Date;import org.quartz.JobExecutionContext;import org.quartz.JobExecutionException;import org.springframework.scheduling.quartz.QuartzJobBean;/** * 可放入Dao层的类作为成员变量,和数据库进行交互.多个Dao类,配置在jobDataAsMap的map标签里 * @author Administrator * */public class SimpleJobDetail extends QuartzJobBean{	@Override	protected void executeInternal(JobExecutionContext context) throws JobExecutionException {		System.out.println(new Date() + "SimpleJobDetail task run.............");	}}