package com.xuyuan.spring3.mvc.helloworld;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.servlet.ModelAndView;/** * http://localhost:8080/start_j2ee/myRoot/the/xuyuan.do * @author Administrator * */@Controller  @RequestMapping("/myRoot")  public class AnnotationControl {  	    @RequestMapping(value = "/the/{name}.do")      public String getName(@PathVariable String name, Model model) {          model.addAttribute("message", "名字：" + name);          return "helloworld";      }        @RequestMapping("/age.do")      public ModelAndView getAge(@RequestParam int age) {          ModelAndView mav = new ModelAndView("helloworld");          mav.addObject("message", "年龄：" + age);          return mav;      }  }