package com.xuyuan.spring3.mvc.helloworld;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.springframework.web.servlet.ModelAndView;import org.springframework.web.servlet.mvc.Controller;/** * 访问:localhost:8080/start_j2ee/helloworld.do * 根据dispatcher-servlet.xml的配置hello.do,交给对应的Controller * 执行这里的代码,ModelAndView转到/spirngmvc/helloworld.jsp * @author Administrator * */public class HelloController implements Controller {		public ModelAndView handleRequest(HttpServletRequest request,HttpServletResponse response) throws Exception {		ModelAndView mav = new ModelAndView("helloworld");		mav.addObject("message", "Hello World!");		return mav;	}}