package com.xuyuan.spring3.mvc.helloworld;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;/** * localhost:8080/start_j2ee/helloworld.do * 利用mvc-annotation转到/spirngmvc/helloworld.jsp * @author Administrator * */@Controllerpublic class HelloWorld {		@RequestMapping(value="helloworld.do")      public void helloworld(Model model){          model.addAttribute("message", "Hello,SpringMVC!");      }  }