package com.xuyuan.spring3.mvc.helloworld;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.springframework.validation.BindException;import org.springframework.web.servlet.ModelAndView;import org.springframework.web.servlet.mvc.SimpleFormController;import com.xuyuan.spring3.mvc.helloworld.model.User;/** * localhost:8080/start_j2ee/reg.do => /spirngmvc/helloworld.jsp * @author Administrator * */public class RegControl extends SimpleFormController {	@SuppressWarnings("deprecation")	public RegControl() {		setCommandClass(User.class);	}	protected ModelAndView processFormSubmission(HttpServletRequest arg0,			HttpServletResponse arg1, Object formbean, BindException arg3)			throws Exception {		User user = (User) formbean;		ModelAndView mav = new ModelAndView("helloworld");		mav.addObject("message", "Hello World!");		mav.addObject("user", user);		return mav;	}	protected ModelAndView showForm(HttpServletRequest arg0,			HttpServletResponse arg1, BindException arg2) throws Exception {		return null;	}}