package com.xuyuan.spring3.mvc.helloworld.model;public class User {	/**	 * @uml.property  name="age"	 */	private int age;	/**	 * @uml.property  name="username"	 */	private String username;		/**	 * @return	 * @uml.property  name="age"	 */	public int getAge() {		return age;	}	/**	 * @param age	 * @uml.property  name="age"	 */	public void setAge(int age) {		this.age = age;	}	/**	 * @return	 * @uml.property  name="username"	 */	public String getUsername() {		return username;	}	/**	 * @param username	 * @uml.property  name="username"	 */	public void setUsername(String username) {		this.username = username;	}}