package com.xuyuan.ssh.entity.base;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;import javax.persistence.TableGenerator;/** * 只需要定义Entity,新增表结构,以及页面的修改而已. * 而Dao,Service,Action中不需要任何代码.当然注解是必须滴. * @author Administrator */@Entity@Table(name = "ssh_beautiful")public class Beautiful {	private Integer id; 	private String name;	private String note;		@Id	@TableGenerator(name="tab_stone",table="generator_table",pkColumnName = "g_key",valueColumnName = "g_value",pkColumnValue="ssh_beautiful_pk",allocationSize=1)	@GeneratedValue(strategy = GenerationType.TABLE,generator="tab_stone")	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getNote() {		return note;	}	public void setNote(String note) {		this.note = note;	}}