package com.xuyuan.ssh.web.base;import java.util.List;import org.apache.struts2.convention.annotation.Result;import org.apache.struts2.convention.annotation.Results;import org.springframework.beans.factory.annotation.Autowired;import com.opensymphony.xwork2.ActionSupport;import com.xuyuan.ssh.common.web.StrutsAction;import com.xuyuan.ssh.entity.base.Role;import com.xuyuan.ssh.service.base.RoleManager;/** * 没有封装基本CRUD功能的StrutsAction时的最简做法.(只有List方法,CRUD可以自行扩展) * 为了复用继承StrutsAction的RoleAction页面role.jsp.添上类的注解@Results. * 当然按照Convention插件的默认配置,可以在WEB-INF/base/下添加simple-role.jsp,simple-role-input.jsp.. * @author Administrator */@Results({	@Result(name="success",location="role.jsp"),	@Result(name=StrutsAction.RELOAD,location=RoleAction.ROLE_ACTION,type=StrutsAction.REDIRECT)})public class SimpleRoleAction extends ActionSupport{	/**	 * @uml.property  name="roleManager"	 * @uml.associationEnd  readOnly="true"	 */	@Autowired	private RoleManager roleManager;		public String execute(){		return SUCCESS;	}		public List<Role> getList(){		return roleManager.getAllRoles();	}}