package com.xuyuan.ssi.model;/** * 使用Spring的jdbcTemplate,则可以不需要此类. * 如果使用Spring的SqlMapClientTemplate,则需要此类和browser-sqlmap.xml * @author Administrator * */public class Browser {	private int statId;	private String statType;	private String statVar;	private int statCount;	public int getStatId() {		return statId;	}	public void setStatId(int statId) {		this.statId = statId;	}	public String getStatType() {		return statType;	}	public void setStatType(String statType) {		this.statType = statType;	}	public String getStatVar() {		return statVar;	}	public void setStatVar(String statVar) {		this.statVar = statVar;	}	public int getStatCount() {		return statCount;	}	public void setStatCount(int statCount) {		this.statCount = statCount;	}	}