package com.xuyuan.ssi.service;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.xuyuan.ssi.dao.user.UserDaoIBatisImpl;import com.xuyuan.ssi.model.User;@Servicepublic class UserService {	/**	 * @uml.property  name="userdao"	 * @uml.associationEnd  	 */	@Autowired	private UserDaoIBatisImpl userdao;	public List<User> getUserList(){		return userdao.getUserList();	}	public List<User> getUserList(int start, int limit){		return userdao.getUserList(start, limit);	}	public int getUserCount(){		return userdao.getUserCount();	}	/**	 * @return	 * @uml.property  name="userdao"	 */	public UserDaoIBatisImpl getUserdao() {		return userdao;	}	/**	 * @param userdao	 * @uml.property  name="userdao"	 */	public void setUserdao(UserDaoIBatisImpl userdao) {		this.userdao = userdao;	}}