package com.xuyuan.ssh.common.hibernate;import org.junit.Before;import org.junit.Test;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;import com.xuyuan.ssh.entity.base.Role;import com.xuyuan.ssh.entity.base.User;import com.xuyuan.ssh.service.base.RoleManager;import com.xuyuan.ssh.service.base.UserManager;public class HibernateDaoTest {	private static UserManager userManager;	private static RoleManager roleManager;	HibernateDao dao = new HibernateDao();		@Before	public void setUp() throws Exception {		ApplicationContext context = new ClassPathXmlApplicationContext("applicationContext_ssh.xml");		userManager = (UserManager)context.getBean("userManager");		roleManager = (RoleManager)context.getBean("roleManager");	}	@Test	public void testAddUser(){		User user = new User();		user.setId(100);		user.setUsername("test100");		user.setPassword("test100");		dao.save(user);		System.err.println("usr:" + user.getUsername() + ";pwd:" + user.getPassword());	}		@Test	public void testGetUser(){		User user = userManager.getUser(1);		System.err.println("usr:" + user.getUsername() + ";pwd:" + user.getPassword());	}		@Test	public void testGetRole(){		Role role = roleManager.getRole(1);		System.err.println("role:" + role.getName());	}}